return items.map(item => {
  const raw = item.json.text; // LLM output JSON string

  let parsed;
  try {
    parsed = JSON.parse(raw);
  } catch (e) {
    parsed = {
      first_name: null,
      last_name: null,
      email: null,
      score: null,
      rationale: "Error parsing response: " + e.message,
      ai_score: null
    };
  }

  return {
    json: {
      ...item.json,
      first_name: parsed.first_name,
      last_name: parsed.last_name,
      email: parsed.email,
      score: parsed.score,
      rationale: parsed.rationale,
      ai_score: parsed.ai_score
    }
  };
});

return items.map(item => {
  const score = item.json.score;
  const aiScore = item.json.ai_score;

  const status = (score >= 7 && aiScore < 5) ? "Shortlisted" : "Rejected";

  return {
    json: {
      ...item.json,
      status
    }
  };
});

return items.map(item => {
  let parsed;

  try {
    parsed = JSON.parse(item.json.text); // or item.json.output depending on your OpenAI setup
  } catch (e) {
    parsed = {
      first_name: '',
      last_name: '',
      email: '',
      email_body: 'Error parsing OpenAI response'
    };
  }

  return {
    json: {
      // Keep only parsed values, drop `text`
      first_name: parsed.first_name,
      last_name: parsed.last_name,
      email: parsed.email,
      email_body: parsed.email_body
    }
  };
});

return items.map(item => {
  const rawBody = item.json.email_body || "";
  const email = item.json.email;
  const subject = "Application Outcome – Hidden Gems";

  // Convert line breaks to HTML tags without adding extra greetings
  const formattedBody = rawBody
    .replace(/\n{2,}/g, '</p><p>')   // two newlines = new paragraph
    .replace(/\n/g, '<br>')          // single newline = line break
    .trim();

  return {
    json: {
      email,
      subject,
      body: `<p>${formattedBody}</p>` // wrap the whole thing cleanly
    }
  };
});

