return items.map(item => {
  const firstName = item.json.first_name || "Candidate";

  const emailBody = `
    <p>Dear <strong>${firstName}</strong>,</p>
    <p>We are pleased to inform you that you have been shortlisted for the next stage of the Hidden Gems selection process. Your profile stood out for its strength and relevance, and we were impressed by the quality of your application.</p>
    <p>To move forward, we kindly ask you to schedule a brief 15-minute meeting with our team at your earliest convenience. You can book a time using the following link:</p>
    <p><a href="https://calendly.com/s-hiddengemtalent/talent15min?month=2025-04&date=2025-04-30">Schedule Your Meeting</a></p>
    <p>If you are unable to find a suitable time on the calendar, please let us know and we will do our best to accommodate you.</p>
    <p>We look forward to speaking with you soon.</p>
    <p>Warm regards,<br><strong>HiddenAI Team</strong></p>
  `;

  return {
    json: {
      ...item.json,
      email_body: emailBody
    }
  };
});

let message = `<p>Hey Suga,</p>
<p>Based on our evaluation, these are the names, scores, and summaries of the successful candidates:</p>`;

items.forEach(item => {
  const name = `${item.json.first_name} ${item.json.last_name}`;
  const score = item.json.score;
  const summary = item.json.rationale;
  const aiScore = item.json.ai_score;

  message += `
    <div style="margin-bottom: 18px;">
      <p><strong>Name:</strong> ${name}<br>
      <strong>Score:</strong> ${score}<br>
      <strong>Summary:</strong> ${summary}<br>
      <strong>AI Score:</strong> ${aiScore}</p>
    </div>
  `;
});

message += `<p>Kind Regards,<br>HiddenAI</p>`;

return [
  {
    json: {
      subject: "Shortlisted Candidate Evaluation",
      body: message
    }
  }
];
